  CREATE OR REPLACE FORCE VIEW "ANDREA"."ASSIST_INDIVIDUALI" ("IDGIOCATORE", "NOME", "COGNOME", "ASSIST") AS 
  SELECT G.IDgiocatore, G.Nome, G.Cognome, COUNT(E.IDevento)
