  CREATE OR REPLACE FORCE VIEW "ANDREA"."VISTA_CLASSIFICA" ("POS", "IDTORNEO", "IDSQUADRA", "SQ", "PTI", "V", "P") AS 
  SELECT ROW_NUMBER() OVER (ORDER BY COALESCE(SUM((PUNTI) + PAREGGI),0) DESC) AS POS, PG.IDTORNEOG, PG.IDSQUADRA, S.NOME AS SQ, COALESCE(SUM((PUNTI) + PAREGGI),0) AS PTI, COALESCE(VITTORIE,0) AS V, COALESCE(PAREGGI,0) AS P 
FROM (SQUADRA S JOIN PARTECIPANTI_GIRONI PG ON S.IDSQUADRA=PG.IDSQUADRA)LEFT OUTER JOIN VITT_PAR  ON PG.IDSQUADRA=VITT_PAR.IDSQUADRA AND PG.IDTORNEOG=VITT_PAR.IDTORNEO
GROUP BY  PG.IDTORNEOG,PG.IDSQUADRA, S.NOME, VITTORIE, PAREGGI
ORDER BY  PTI DESC;
