--  DDL for View VITT_PAR
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "ANDREA"."VITT_PAR" ("IDSQUADRA", "SQUADRA", "VITTORIE", "PAREGGI", "IDTORNEO", "PUNTI") AS 
  SELECT IDSQ, SQUADRA, COALESCE(SUM(VITT),0) AS VITTORIE, COALESCE(PAREGGI,0), IDTORNEO, COALESCE(SUM(VITT)*3,0) AS PUNTI
FROM    ((SELECT IDSQ2 AS IDSQ, SQUADRA2 AS SQUADRA, IDTORNEOG AS IDTORNEO, COALESCE(COUNT(GOL2),0) AS VITT
       FROM VISTA_RIS_PARTITE
       WHERE IDTORNEOG IS NOT NULL AND GOL2>GOL1
       GROUP BY IDSQ2,SQUADRA2,IDTORNEOG)
    UNION ALL
      (SELECT IDSQ1, SQUADRA1, IDTORNEOG, COALESCE(COUNT(GOL1),0)
      FROM VISTA_RIS_PARTITE
      WHERE IDTORNEOG IS NOT NULL AND GOL2<GOL1
    GROUP BY IDSQ1,SQUADRA1,IDTORNEOG)
    ) LEFT OUTER JOIN (SELECT IDSQ1 AS IDSQUADRA, IDTORNEOG, COALESCE(SUM(PARE),0) AS PAREGGI
                      FROM ((SELECT IDSQ1 ,IDTORNEOG, COALESCE(COUNT(GOL1),0) AS PARE
                                FROM VISTA_RIS_PARTITE 
                                WHERE IDTORNEOG IS NOT NULL AND GOL2=GOL1
                                GROUP BY IDSQ1,IDTORNEOG)
                      UNION ALL
                      (SELECT IDSQ2, IDTORNEOG, COALESCE(COUNT(GOL1),0) AS PARE
                                FROM VISTA_RIS_PARTITE 
                                WHERE IDTORNEOG IS NOT NULL AND GOL2=GOL1
                                GROUP BY IDSQ2,IDTORNEOG))
                      GROUP BY IDSQ1, IDTORNEOG) ON IDSQUADRA=	IDSQ
GROUP BY IDSQ, SQUADRA, PAREGGI, IDTORNEO;
